-- ----------------------------
-- View structure for vcollandpoem
-- ----------------------------
DROP VIEW IF EXISTS `vcollandpoem`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vcollandpoem` AS select `t_collection`.`id` AS `id`,`t_collection`.`email` AS `email`,`t_collection`.`poetryid` AS `poetryid`,`t_collection`.`time` AS `time`,`t_poetry`.`name` AS `poetryname`,`t_poetry`.`typeid` AS `typeid`,`t_poetry`.`authoruid` AS `authoruid`,`t_poetrytype`.`type` AS `type`,`t_poet`.`name` AS `poetname` from (((`t_collection` join `t_poetry` on((`t_collection`.`poetryid` = `t_poetry`.`id`))) join `t_poetrytype` on((`t_poetry`.`typeid` = `t_poetrytype`.`id`))) join `t_poet` on((`t_poetry`.`authoruid` = `t_poet`.`id`))) ;

-- ----------------------------
-- View structure for vpoet
-- ----------------------------
DROP VIEW IF EXISTS `vpoet`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vpoet` AS select `t_poet`.`id` AS `id`,`t_poet`.`name` AS `name`,`t_poet`.`name_zi` AS `name_zi`,`t_poet`.`name_hao` AS `name_hao`,`t_poet`.`gender` AS `gender`,`t_poet`.`birthday` AS `birthday`,`t_poet`.`deathday` AS `deathday`,`t_poet`.`intro` AS `intro`,`t_poet`.`masterwork` AS `masterwork`,`t_poet`.`dynastyid` AS `dynastyid`,`t_dynasty`.`name` AS `dynastyname` from (`t_poet` join `t_dynasty`) where (`t_poet`.`dynastyid` = `t_dynasty`.`id`) ;

-- ----------------------------
-- View structure for vpoetry
-- ----------------------------
DROP VIEW IF EXISTS `vpoetry`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vpoetry` AS select `t_poetry`.`id` AS `id`,`t_poetry`.`name` AS `name`,`t_poetry`.`dynastyid` AS `dynastyid`,`t_poetry`.`typeid` AS `typeid`,`t_poetry`.`authoruid` AS `authoruid`,`t_poetry`.`content` AS `content`,`t_poetry`.`annotation` AS `annotation`,`t_poetry`.`translation` AS `translation`,`t_dynasty`.`name` AS `dynastyname`,`t_poet`.`name` AS `poetname`,`t_poetrytype`.`type` AS `type` from (((`t_poet` join `t_poetry` on((`t_poet`.`id` = `t_poetry`.`authoruid`))) join `t_dynasty` on((`t_poetry`.`dynastyid` = `t_dynasty`.`id`))) join `t_poetrytype` on((`t_poetrytype`.`id` = `t_poetry`.`typeid`))) ;

-- ----------------------------
-- Procedure structure for recommend
-- ----------------------------
DROP PROCEDURE IF EXISTS `recommend`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `recommend`(IN arg int)
BEGIN
select temp.poetryid,t_poetry.`name`,t_poetry.authoruid,t_poetry.content,t_poetry.typeid,t_poetry.dynastyid,
t_dynasty.name as dynastyname,t_poet.name as poetname,num,t_poetrytype.type
from
(select COUNT(t_collection.poetryid)as num,poetryid
from t_collection
group by t_collection.poetryid
order by num desc 
limit 100)as temp 
left join t_poetry ON t_poetry.id=temp.poetryid
left join t_dynasty on t_dynasty.id=t_poetry.dynastyid 
left join t_poet on t_poetry.authoruid=t_poet.id
left join t_poetrytype on t_poetrytype.id=t_poetry.typeid;

END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `t_insert_tcollection`;
DELIMITER ;;
CREATE TRIGGER `t_insert_tcollection` BEFORE INSERT ON `t_collection` FOR EACH ROW BEGIN
	if ((select count(*) from t_collection where poetryid=new.poetryid and email=new.email) >0) then 
              signal sqlstate 'HY000' set message_text = '请勿重复收藏同一个诗文' ;
	end if;
END
;;
DELIMITER ;
